;(function pluginBuilder($,undefined){$.fn.ghostText=function(opts){var $this=$(this);function setupGhost(opts){if(!opts){opts=$(this).data()||{};window.console&&console.log(opts);}var defaults={live:false,cssClass:'ghost'};var options=$.extend(opts,defaults);var whenClicked=function(){var text=$(this).val();var hasText=text.length>0;if(!hasText||(hasText&&$.trim(text)===options.ghostText)){$(this).val('').removeClass(options.cssClass)}};var whenBlurred=function(){var text=$(this).val();var hasText=text.length>0;if(!hasText||(hasText&&$.trim(text)===options.ghostTexts)){$(this).val(options.ghostText).addClass(options.cssClass);}};if(options.live){$(this).live('focus',whenClicked).live('blur',whenBlurred);}else{$(this).focus(whenClicked).blur(whenBlurred);}whenBlurred.call(this);}if($this.size()===0){return;}else if($this.size()>1){$this.each(function(){setupGhost.call(this,opts);});}else{setupGhost.call($this,opts);}};})(jQuery);
